mapper.select:
SELECT o.org_id as orgId, o.org_name AS orgName, COUNT(1) AS tbNeed, SUM(CASE t.states WHEN 3 THEN 1 ELSE 0 END) AS slNeed, SUM(CASE t.states WHEN 2 THEN 1 ELSE 0 END) AS juNeed, SUM(CASE t.states WHEN 4 THEN 1 ELSE 0 END) AS dslNeed, SUM(CASE t.states WHEN 1 THEN 1 ELSE 0 END) AS wcNeed, sum(CASE WHEN ( SELECT a.states FROM gome_gmp_res_need a where DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= date(a.create_time) AND a.create_time <=NOW() AND a.need_id=t.need_id )=1 THEN 1 ELSE 0 END ) as yNeed FROM gome_gmp_res_need t,gome_gmp_res_user b, gome_gmp_res_org o WHERE t.create_user=b.id and length(b.org_id) >= 4 AND o.org_id=left(b.org_id,4)
AND DATE_FORMAT(t.create_time,'%Y-%m-%d') >= #{startTimes}
AND DATE_FORMAT(t.create_time,'%Y-%m-%d') <= #{endTimes}
AND EXISTS (SELECT u.id FROM gome_gmp_res_user u,gome_gmp_res_org o WHERE u.org_id = o.org_id AND u.id = t.create_user and
o.org_id LIKE CONCAT(#{item},'%')
)
GROUP BY orgName

select orgId,org_name, sum(jxz) AS jxz, sum(ysx) as ysx, sum(fx) as fx, sum(yq) as yq FROM (SELECT o.org_id as orgId, o.org_name, ( SELECT count(1) FROM gome_gmp_res_project p WHERE p.schedule_id NOT IN(0, 5, 7, 9, 10, 11) AND p.pro_id = pro.pro_id )jxz, ( SELECT count(1) FROM gome_gmp_res_project p, gome_gmp_res_org o, gome_gmp_res_user u WHERE 1 = 1 AND p.bp_id = u.id AND u.org_id = o.org_id AND p.schedule_id = 5 AND p.pro_id = pro.pro_id )ysx, ( SELECT count(1) FROM gome_gmp_res_project c, gome_gmp_res_org o, gome_gmp_res_user u WHERE 1 = 1 AND c.bp_id = u.id AND o.org_id = u.org_id AND c.schedule_id = 3 AND c.pro_id = pro.pro_id )fx, ( SELECT count(1) FROM gome_gmp_res_project c, gome_gmp_res_org o, gome_gmp_res_user u WHERE 1 = 1 AND c.bp_id = u.id AND o.org_id = u.org_id AND c.schedule_id =4 AND c.pro_id = pro.pro_id )yq FROM gome_gmp_res_project pro, gome_gmp_res_user b, gome_gmp_res_org o WHERE pro.bp_id = b.id and length(b.org_id) >= 4 AND o.org_id=left(b.org_id,4) and pro.is_commit=1
AND pro.update_time >= #{startTime}
AND pro.update_time <= #{endTime}
)t WHERE t.jxz!=0 OR t.ysx!=0 or t.fx!=0 or t.yq!=0

SELECT o.org_id as orgId, o.org_name, count(1) num FROM ( SELECT p.pro_id, p.schedule_id, p.update_time FROM gome_gmp_res_project p UNION ALL SELECT pro_id, 13 schedule_id, update_time FROM gome_gmp_res_project ) tj, gome_gmp_res_user u, gome_gmp_res_project p, gome_gmp_res_org o WHERE 1=1 and p.bp_id = u.id and length(u.org_id) >= 4 AND o.org_id=left(u.org_id,4) AND tj.pro_id=p.pro_id
AND tj.update_time >= #{startTime}
AND tj.update_time <= #{endTime}
AND EXISTS (SELECT u.id FROM gome_gmp_res_user u,gome_gmp_res_org o, gome_gmp_res_project p WHERE u.org_id = o.org_id AND u.id = p.bp_id and p.pro_id=tj.pro_id and
o.org_id LIKE CONCAT(#{item},'%')
)
AND tj.schedule_id=#{scheduleId} AND p.pro_type = #{proType} AND p.is_commit=1 group By o.org_name

SELECT p.pro_id,p.title,(select item_name from gome_gmp_dict_item where group_type = 'statusId' and item_id = p.status_id) status_name, (select item_name from gome_gmp_dict_item where group_type = 'priorityId' and item_id = p.priority_id) priority_name, (select item_name from gome_gmp_dict_item where group_type = 'scheduleId' and item_id = p.schedule_id) schedule_name, (select item_name from gome_gmp_dict_item where group_type = 'actualize' and item_id = p.actualize) actualize_name, CONCAT(IFNULL(( select round(sum(work_period/(select sum(k.work_period) sum_period from gome_gmp_res_task k where k.pro_id = s.pro_id)*s.`schedule`/100)*100,1) from gome_gmp_res_task s where s.pro_id = p.pro_id ),0),'%') percentage, p.plan_time, (select user_name from gome_gmp_res_user where id = p.bp_id) bp_name FROM gome_gmp_res_org o, gome_gmp_res_user u, gome_gmp_res_project p, ( SELECT p.pro_id, p.schedule_id, p.update_time FROM gome_gmp_res_project p UNION ALL SELECT pro_id, 13 schedule_id, update_time FROM gome_gmp_res_project ) tj WHERE 1=1 and o.org_id=u.org_id AND u.id=p.bp_id and tj.pro_id=p.pro_id
AND tj.update_time >= #{startTime}
AND tj.update_time <= #{endTime}
AND EXISTS (SELECT u.id FROM gome_gmp_res_user u,gome_gmp_res_org o, gome_gmp_res_project p WHERE u.org_id = o.org_id AND u.id = p.bp_id and p.pro_id=tj.pro_id and
o.org_id LIKE CONCAT(#{item},'%')
)
AND tj.schedule_id=#{scheduleId} AND p.pro_type=#{proType} and p.is_commit=1

SELECT p.pro_id,p.title,(select item_name from gome_gmp_dict_item where group_type = 'statusId' and item_id = p.status_id) status_name, (select item_name from gome_gmp_dict_item where group_type = 'priorityId' and item_id = p.priority_id) priority_name, (select item_name from gome_gmp_dict_item where group_type = 'scheduleId' and item_id = p.schedule_id) schedule_name, (select item_name from gome_gmp_dict_item where group_type = 'actualize' and item_id = p.actualize) actualize_name, CONCAT(IFNULL(( select round(sum(work_period/(select sum(k.work_period) sum_period from gome_gmp_res_task k where k.pro_id = s.pro_id)*s.`schedule`/100)*100,1) from gome_gmp_res_task s where s.pro_id = p.pro_id ),0),'%') percentage, p.plan_time, (select user_name from gome_gmp_res_user where id = p.bp_id) bp_name from gome_gmp_res_project p, gome_gmp_res_org o, gome_gmp_res_user u WHERE 1=1 AND p.bp_id=u.id and u.org_id= o.org_id and p.schedule_id=5 and p.is_commit=1
AND p.update_time >= #{startTime}
AND p.update_time <= #{endTime}
AND EXISTS (SELECT u.id FROM gome_gmp_res_user u,gome_gmp_res_org o WHERE u.org_id = o.org_id AND u.id = p.bp_id and
o.org_id LIKE CONCAT(#{item},'%')
)
and p.schedule_id=#{scheduleId} GROUP BY o.org_name,p.pro_id

SELECT p.pro_id,p.title,(select item_name from gome_gmp_dict_item where group_type = 'statusId' and item_id = p.status_id) status_name, (select item_name from gome_gmp_dict_item where group_type = 'priorityId' and item_id = p.priority_id) priority_name, (select item_name from gome_gmp_dict_item where group_type = 'scheduleId' and item_id = p.schedule_id) schedule_name, (select item_name from gome_gmp_dict_item where group_type = 'actualize' and item_id = p.actualize) actualize_name, CONCAT(IFNULL(( select round(sum(work_period/(select sum(k.work_period) sum_period from gome_gmp_res_task k where k.pro_id = s.pro_id)*s.`schedule`/100)*100,1) from gome_gmp_res_task s where s.pro_id = p.pro_id ),0),'%') percentage, p.plan_time, (select user_name from gome_gmp_res_user where id = p.bp_id) bp_name FROM gome_gmp_res_project p, gome_gmp_res_user b, gome_gmp_res_org o WHERE 1=1 AND p.bp_id=b.id AND b.org_id=o.org_id
AND p.update_time >= #{startTime}
AND p.update_time <= #{endTime}
AND EXISTS (SELECT u.id FROM gome_gmp_res_user u,gome_gmp_res_org o WHERE u.org_id = o.org_id AND u.id = p.bp_id AND
o.org_id LIKE CONCAT(#{item},'%')
)
and p.schedule_id not in(0,5,7,9,10,11) and p.is_commit=1

SELECT p.pro_id,p.title,(select item_name from gome_gmp_dict_item where group_type = 'statusId' and item_id = p.status_id) status_name, (select item_name from gome_gmp_dict_item where group_type = 'priorityId' and item_id = p.priority_id) priority_name, (select item_name from gome_gmp_dict_item where group_type = 'scheduleId' and item_id = p.schedule_id) schedule_name, (select item_name from gome_gmp_dict_item where group_type = 'actualize' and item_id = p.actualize) actualize_name, CONCAT(IFNULL(( select round(sum(work_period/(select sum(k.work_period) sum_period from gome_gmp_res_task k where k.pro_id = s.pro_id)*s.`schedule`/100)*100,1) from gome_gmp_res_task s where s.pro_id = p.pro_id ),0),'%') percentage, p.plan_time, (select user_name from gome_gmp_res_user where id = p.bp_id) bp_name FROM gome_gmp_res_project p,gome_gmp_res_user u,gome_gmp_res_org o WHERE 1 = 1 and p.bp_id=u.id and u.org_id=o.org_id
AND p.update_time >= #{startTime}
AND p.update_time <= #{endTime}
AND EXISTS (SELECT u.id FROM gome_gmp_res_user u,gome_gmp_res_org o WHERE u.org_id = o.org_id AND u.id = p.bp_id and
o.org_id LIKE CONCAT(#{item},'%')
)
and p.schedule_id=#{scheduleId} and p.is_commit=1
